create table dept (dept_seq bigint generated by default as identity, depth integer not null, dept_cd varchar(255), dept_name varchar(255), primary key (dept_seq));
create table employee (emp_no bigint generated by default as identity, address varchar(255), birth_date varchar(255), email varchar(255), join_date date, name varchar(255), reti_date date, salary integer, dept_seq bigint, primary key (emp_no));
